mapper.select:
SELECT E.*, D.`name` AS departName, R.`name` AS rankName , J.`name` AS jobName
FROM T_EMPLOYEE E, T_JOB J, T_DEPARTMENT D, T_RANK R WHERE E.department=D.id AND E.job=J.id AND E.rank=R.id

    AND E.name LIKE concat(#{name},'%')


    AND E.nation = #{nation}


    AND E.department = #{department}


    AND E.job = #{job}


    AND E.rank = #{rank}


    limit ${pageIndex}, ${pageSize}

SELECT COUNT(1)
FROM T_EMPLOYEE E, T_JOB J, T_DEPARTMENT D, T_RANK R WHERE E.department=D.id AND E.job=J.id AND E.rank=R.id

    AND E.name LIKE concat(#{name},'%')


    AND nation = #{nation}


    AND department = #{department}


    AND job = #{job}


    AND rank = #{rank}
